mapper.update:
UPDATE PAYMENTS 
	SET DELFLAG = 'Y', UPDATE_DATE = SYSDATE 
	WHERE PAYCODE = #{paycode}

UPDATE PAYMENTS 
	SET CANCELYN = 'N', UPDATE_DATE = SYSDATE 
	WHERE PAYCODE = #{paycode}

UPDATE PAYMENTS 
	SET PAY_STATE = 'cancelled', CANCEL_DATE = SYSDATE, CANCEL_DESC = #{cancel_desc}, UPDATE_DATE = SYSDATE 
	WHERE PAYCODE = #{paycode} AND IMP_UID = #{imp_uid}
